mapper.select:
select oct.id, oct.ocpus, oct.`memory`, oct.disk, oct.architecture, oct.interval, oct.create_numbers, oct.operation_system, oct.create_time, ou.username from oci_create_task oct left join oci_user ou on oct.user_id = ou.id
and oct.architecture = #{architecture}
and ou.username like '%' || #{keyword} || '%' or ou.oci_region like '%' || #{keyword} || '%'
order by ou.username, oct.create_time DESC limit #{offset}, #{size}

select count(1) from oci_create_task oct left join oci_user ou on oct.user_id = ou.id
and oct.architecture = #{architecture}
and ou.username like '%' || #{keyword} || '%' or ou.oci_region like '%' || #{keyword} || '%'
